    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        boolean sucesso = false;
        
        try{
            ModeloImpressora x = new ModeloImpressora();
            x.setModeloImpressora(this.jTextImpressora.getText());
            x.setModeloToner(this.jTextToner.getText());
            x.setPrecoToner(Double.parseDouble(this.jTextPreco.getText().replace(',', '.')));
            sucesso = this.sistema.cadastrarModeloImpressora(x);
            
        }catch (NumberFormatException e){
            Alertas.erroFormatoEntrada(this);
        } catch (SQLException ex) {
            Alertas.erroBanco(this,ex.toString());
        }
        Alertas.sucessoOuErro(this,sucesso);
        if(sucesso) this.dispose();
    }//GEN-LAST:event_jButton1ActionPerformed


